mapper.select:
select COUNT(*) from BOARD where STATUS = 'Y'
and BOARDTITLE like '%${keyword}%'
and USERID = #{keyword}

mapper.insert:

insert into BOARD(
				BOARDTITLE
				, BOARDCONTENT
				, USERID

				, ATTACHMENT

				)

		values(
				#{boardTitle}
				, #{boardContent}
				, #{userId}

				, #{attachment}

				);

insert into ATTACHMENT(
					ORIGINNAME
					, STOREDNAME
					, PATH
					, STATUS
					, BOARDNO
					, USERID
					)
			values(
					#{originName}
					, #{storedName}
					, #{path}
					, #{status}
					, #{boardNo}
					, #{userId}
					);

mapper.select:
select	*
from 	ATTACHMENT
where	BOARDNO = #{boardNo}
and 	STATUS = 'Y';

mapper.update:

update	BOARD
set 	VIEWCOUNT = VIEWCOUNT + 1 
where	BOARDNO = #{boardNo};

update	BOARD
set		STATUS = 'N' 
where	BOARDNO = #{boardNo};

update	ATTACHMENT
set 	STATUS = 'N'
where	BOARDNO = #{boardNo}

	update	BOARD, ATTACHMENT 
    set 	BOARD.ATTACHMENT = 'N'
    		, ATTACHMENT.STATUS ='N' 
    WHERE 	BOARD.BOARDNO = ATTACHMENT.BOARDNO 
    and 	BOARD.BOARDNO = #{boardNo} 
    and 	ATTACHMENT.FILENO = #{fileNo};

update	BOARD
set		BOARDTITLE = #{boardTitle}
		, BOARDCONTENT = #{boardContent}
		, ATTACHMENT = #{attachment}
where 	BOARDNO = #{boardNo};
